import React, {useState} from 'react';

function App() {
  const [text, setText] = useState('Song');

  const onSubmit = ()=>{
    alert('submitted');
  }

  const onKeyUp = (e)=>{
    if(e.keyCode === 13){
      onSubmit();
    }
  }

  // let text = 'Song';

  const updateTxt = ()=>{
    // text = 'Jae';
    setText('Jae');
    console.log(text);
  }
  return (

    <div className="App">
      <input onKeyUp = {onKeyUp} type="text"/>
      <button onClick={onSubmit}> Submit</button>

      <br/><br/><hr/>

      <span>{text}</span>
      <button onClick={updateTxt}>Update</button>

    </div>

  );
}

export default App;
