import React, {useState, useEffect} from 'react';
import Counter from './components/counter';
import MovieList from './components/movieList';
import MovieForm from './components/movieForm';


function App() {
  // 초반 영화리스트를 빈 배열로 만든다.
  const [movies, setMovies] = useState([])

  // 영화 삭제를 위한 함수
  // filter -> map 과 비슷한 형식으로 모든 요소를 비교하여 true값만 리턴한다.
  // filter를 이용하여 id가 일치하지 않는것을 찾아내어 true로 리턴시킨다.
  const removeMovie = (id) =>{
    setMovies(movies.filter(movie =>{
      return movie.id !== id;
    }))
  };

  const renderName = movies.length ? movies.map(movie =>{
    return(
      <MovieList
      movie={movie}
      key={movie.id}
      removeMovie={removeMovie}
      />
    );
  }) : "추가된 영화가 없습니다."

  const updateMovie = (movie) =>{
    setMovies(
      [
        // ...movies 는 앞의 배열의 그대로 가져온다. (구조 분해 할당)
        ...movies,
        movie
      ]
    );
  }

  return (
    <div className="App"> 
      <h1>Movie List</h1>
      <MovieForm updateMovie={updateMovie}/>
      {renderName}
    </div>
  );

}

export default App;
